import React, {useState} from "react";

function App() {

    const [calc , setCalc] = useState("");
    const [result , setResult] = useState("");

    const ops = ["+" , ]

    const createNumbers = () => {
        const numbers = [];
            for (let i=1; i<10; i++) {
                numbers.push(
                <button key={i}> {i} </button>
                )
            }

            return numbers;
    };

    return  (
        <div className="calculator">
            <div className="container">
                <div className="display">
                    <span>(0)</span>0
                </div>
                <div className="operators">
                    <button>+</button>
                    <button>-</button>
                    <button>*</button>
                    <button>/ </button>
                    <button>DEL</button>
                </div>
                    <div className="digits">
                        { createNumbers() }
                        <button>0</button>
                        <button>.</button>
                        <button>=</button>
                    </div>
            </div>
        </div>
    )
}


export default App;
